var mysql      = require('mysql');
var connection = mysql.createConnection({
    host     : 'localhost',
    user     : 'root',
    password : '',
    database : 'fxos_players'
});

connection.connect(function(err) {
    console.log('MySQL', 'error');
});

exports.insert = function(item, success, error){
    var query = connection.query('INSERT INTO nerdcast SET ?', item, success, error);
}

exports.list = function(success, error){
    var queryString = 'SELECT * FROM nerdcast';
    connection.query(queryString, function(err, rows, fields) {
        if (err) throw err;

        for (var i in rows) {
            console.log('Nerdcast Titles: ', rows[i].title);
        }
    });
}