var mongo = require('mongodb');
var monk = require('monk');
var db =  monk('localhost:27017/firefoxos');
var podcastCollection = db.get('podcast');

exports.insert = function(item, success, error){

    podcastCollection.insert(item, function(err, doc){
        if(err){
            error(err)
        }else{
            success(doc);
        }
    });

}

exports.get = function(podcastModel, success, error){

    podcastCollection.find({podcast:podcastModel.podcast, title: podcastModel.title}, function(err,doc){
        if(err){
            error(err);
        }else if(doc){
            success(doc);
        }

    })

}